    protected override async Task OnInitializedAsync()
    {
        Busy = true;

        try
        {
            Context = DbFactory.CreateDbContext();
            Contact = await Context.Contacts
                .SingleOrDefaultAsync(c => c.Id == ContactId);
        }
        finally
        {
            Busy = false;
        }

        await base.OnInitializedAsync();
    }
